import axios from 'axios';
import React, { useEffect, useState } from 'react'
import Comment from '../Comment/Comment'
import "./Comments.css"
const Comments = ({videoId}) => {

  const [comments,setComments]=useState([]);

  useEffect(()=>{

    const fetchComments = async() =>{
      try {
        const res = await axios.get(`/comments/${videoId}`)
        setComments(res.data)
      } catch (error) {
        
      }
    }
    fetchComments();
  },[videoId])


  return (
    <div className='comments_cont'>
      <div className='newcomment'>
        <img className='avatar' src="https://yt3.ggpht.com/yti/APfAmoE-Q0ZLJ4vk3vqmV4Kwp0sbrjxLyB8Q4ZgNsiRH=s88-c-k-c0x00ffffff-no-rj-mo" alt='...'/>
        <input className='Input' placeholder="Add a comment..." />
      </div>

      {comments.map(comment=>{

      <Comment  key={comment._id}  comment={comment}/>
      })}
      
    </div>
  )
}

export default Comments
