import React, { useEffect, useState } from 'react'
import { Link } from "react-router-dom";
import "./Card.css"
import TimeAgo from 'javascript-time-ago'
import axios from "axios";
const timeAgo = new TimeAgo('en-US')
import {format} from "timeago.js"
const Card = ({video}) => {
  const [channel,setChannel]=useState({})

  useEffect(()=>{
    const fetchchannel = async()=>{
      const res = await axios.get(`/user/find/${video.userId}`)
      setChannel(res.data)
    }
    fetchchannel();
  },[video.userId])
  return (
    <Link to="/video/text" style={{textDecoration:"none"}}>
    <div className='card_cont'>
        <img className='card_img' type="" src={video.imgUrl} alt="..."/>
        <div className='card_details'>
        
                <img className='card_channelImg'src={channel.img} alt='...'/>
            
            <div className='card_texts'>
                <div className='card_title'>{video.title}</div>
                <div className='card_channelname'>{channel.name}</div>
                <div className='card_info'>{video.views} views â€¢ {format(video.createdAt)}</div>
            </div>
        </div>
      
    </div>
    </Link>
  )
}

export default Card
